components.calculateValue:
items = [];
let PlatformFees = form.app.PlatformFees;
console.info(PlatformFees);
let PrimaryTakePercent = (PlatformFees.PlatformPrimarySaleTake/100);
let SecondaryTakePercent = (PlatformFees.PlatformSecondarySaleTake/100);

if(_.isNumber(data.details.salePrice)) {
  var secondarySales = 0
  var initialSales = 0
  if(_.isArray(data.properties.royalty))
  data.properties.royalty.forEach((royalty,key) => {
    sale ={};
    sale["participant"] = royalty["participant"]
    if(royalty["initialSalePercent"] > 0)
    sale["initialSale"] = (royalty["initialSalePercent"] * (1 - PrimaryTakePercent) *0.01 * data.details.salePrice).toFixed(2);
    if(royalty["secondarySalePercent"] > 0)
    sale["secondarySale"] = (royalty["secondarySalePercent"] * (1 - SecondaryTakePercent) *0.01 * data.details.salePrice).toFixed(2);
    if(royalty["initialSalePercent"] > 0 || royalty["secondarySalePercent"] > 0)
    items.push(sale)
    secondarySales += royalty["secondarySalePercent"] * (1 - SecondaryTakePercent) *0.01 * data.details.salePrice
    initialSales += royalty["initialSalePercent"] * (1 - PrimaryTakePercent) *0.01 * data.details.salePrice
  })
  let sale1 ={};
  sale1["participant"] = "Seller"
  if(!isNaN(initialSales))
  sale1["initialSale"] = ((((1 - PrimaryTakePercent) * data.details.salePrice) ) - initialSales).toFixed(2);
  if(!isNaN(secondarySales))
  sale1["secondarySale"] = ((((1 - SecondaryTakePercent) * data.details.salePrice))- secondarySales).toFixed(2);
  let sale2 ={};
  sale2["participant"] = "Platform Fees"
  sale2["initialSale"] = ((PrimaryTakePercent * data.details.salePrice)).toFixed(2);
  sale2["secondarySale"] = ((SecondaryTakePercent * data.details.salePrice)).toFixed(2);
  items.push(sale2)
  items.push(sale1)
}
value = items;
